<template>    <div class="form-group">        <div :id="'reply-'+id" class="card">            <div class="card-header">                <div class="level" style="align-items: center;display: flex">                    <h4 class="flex" style="flex: 1">                        <a :href="'/profiles/' + data.owner.name" v-text="data.owner.name">                        </a> said <span v-text="ago">                    </span>                    </h4>                    <div v-if="signIn">                        <favorite :reply="data"></favorite>                    </div>                </div>            </div>            <div class="card-body">                <div v-if="editing">                    <div class="form-group">                        <textarea class="form-control" v-model="body"></textarea>                    </div>                    <button class="btn btn-primary btn-sm mr-1" @click="update">save</button>                    <button class="btn btn-link btn-sm mr-1" @click="editing = false">cancel</button>                </div>                <div v-else v-text="body">                </div>            </div>            <div class="card-footer level" v-if="canUpdate">                <button class="btn btn-primary btn-sm mr-1" @click="editing = true">Edit</button>                <button class="btn btn-danger btn-sm" @click="destroy">Delete</button>            </div>        </div>    </div></template><script>    import Favorite from './Favorite.vue';    import moment from 'moment';    export default {        props: ['data'],        components: {Favorite},        data() {            return {                editing: false,                body: this.data.body,                id: this.data.id            };        },        computed: {            ago() {                return moment(this.data.created_at).fromNow();            },            signIn() {                return window.App.signIn;            },            canUpdate() {                return this.authorize(user => this.data.user_id == user.id);            }        },        methods: {            update() {                axios.patch('/replies/' + this.data.id, {                    body: this.body                });                this.editing = false;                flash('updated!');            },            destroy() {                axios.delete('/replies/' + this.data.id);                this.$emit('deleted', this.data.id)            },            scroll() {                window.scrollTo(0, 0)            }        }    }</script>